import React, { useState } from 'react';
import Button from '../button/Button';
import InputField from '../input/InputField';
import './loginForm.css';


interface LoginFormProps {
  onLogin: ({
    username,
    password,
  }: {
    username: string;
    password: string;
  }) => void;
  title?: string;
  errorMessage?: string;

}

const LoginForm: React.FC<LoginFormProps> = ({ onLogin, title = 'Log In', errorMessage }) => {
  const [submitted, setSubmitted] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleFormSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    if (username && password) {
      onLogin({ username, password });
    }
    setSubmitted(true);
  };

  return (
    <div className="max-w-screen-sm p-12 mx-auto bg-gray-50 rounded-md shadow-lg">
      <form className="flex flex-col" onSubmit={handleFormSubmit}>
        <fieldset>
          <legend className="text-3xl text-gray-800 mb-4">{title}</legend>

          <InputField
            name="Username"
            type="text"
            label="Username"
            submitted={submitted}
            requiredMessage='Username is required'
            onChange={(e) => setUsername(e.target.value)}
            value={username}
            autoComplete="username"
          />
          <InputField
            name="Password"
            type="password"
            label="Password"
            submitted={submitted}
            requiredMessage='Username is required'
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            autoComplete="current-password"
          />
          <Button 
          primary
          label='LoginForm Button'
          size = 'large'
          backgroundColor='#1ea7fd'        
          />

          {errorMessage && (
            <div className="text-red-500 mt-2">{errorMessage}</div>
          )}

        </fieldset>
      </form>
    </div>
  );
};


export default LoginForm;
